import React from 'react';
import { useEffect, useState } from 'react';
import "./Home.scss"
import panner from '../../images/panner.png'
import productData from '../../data/Product';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';

const Home = () => {
    console.log(productData)
    const listItem = productData.getProducts(5)
    // const listItems = listItem.map((item)=> 
    //     <div className='products'>
    //         <div className='card' key={item.id}>
    //             <div className='card_img'>
    //                 <img src={item.image01} />
    //                 {/* <img src={item.image02} />
    //                 <img src={item.image03} /> */}
    //             </div>
    //             <div className='card_header'>
    //                 <h4>{item.title}</h4>
    //                 <p>{item.categorySlug}</p>
    //                 <p className='price'>{item.price}</p>
    //                 <div className='btn'>Add to cart</div>
    //             </div>
                
    //         </div>
    //     </div>
        
    // )
    return (
        <div className='home'>
            <div className='home_container'>
                <img src={panner} className="panner"></img>
            </div>
            <h3>Trending Now</h3>
            
            <div className='Products'>
                {listItem.map((item)=> 
                    <div className='product'>
                        <div className='card' key={item.id}>
                            <div className='card_img'>
                                <img src={item.image01} className="images" />
                            </div>
                            <div className='card_header'>
                                <h4>{item.title}</h4>
                                <p>{item.categorySlug}</p>
                                <p className='price'>{item.price}</p>
                                <div className='btn'><AddShoppingCartIcon /></div>
                            </div>
                            
                        </div>
                    </div>
                )}
                {/* // {listItems} */}
            </div>

            <div className='panner'>
                <div className='information'>
                    <h1 className='infor'>FOREVER 21</h1>
                    <h3 className='infor'>Big Fashion Festival</h3>
                    <h3 className='infor'>70% - 80% off</h3>
                    <button className='infor'>Explore</button>
                </div>
                
            </div>
        </div>
    )


}

export default Home
