import React from 'react';
import { useEffect, useState } from 'react';
import "./Home.scss"
import panner from '../../images/panner.png'
import productData from '../../data/Product';
import { Grid } from '@mui/material';

const Home = () => {
    console.log(productData)
    const listItem = productData.getProducts(6)
    const listItems = () =>{
        <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
            {listItem.map((product)=> (
                <Grid item key={product.id} xs={12} sm={6} md={4}>
                    <div className='card' key={product.id}>
                        <div className='card_img'>
                            <img src={product.image01} />
                            {/* <img src={item.image02} />
                            <img src={item.image03} /> */}
                        </div>
                        <div className='card_header'>
                            <h4>{product.title}</h4>
                            <p>{product.categorySlug}</p>
                            <p className='price'>{product.price}</p>
                            <div className='btn'>Add to cart</div>
                        </div>
                        
                    </div>
                </Grid>  
            ))}
        </Grid>
    }
    
    console.log("product")
    console.log(listItems)
    return (
        <div className='home'>
            <div className='home_container'>
                <img src={panner} className="panner"></img>
            </div>
            <div className='Products'>
                <h3>Trending Now</h3>
                {listItems}
            </div>
        </div>
    )


}

export default Home
