import React from 'react';
import { useEffect, useState } from 'react';
import "./Home.scss"
import panner from '../../images/panner.png'
import productData from '../../data/Product';
import { Grid } from '@mui/material';

const Home = () => {
    console.log(productData)
    const listItem = productData.getProducts(Math.floor(Math.random() * 21))
    const listItems = listItem.map((item)=> 
        <Grid item xs={2} sm={4} md={4} key={item.id}>
            <div className='card' key={item.id}>
                <div className='card_img'>
                    <img src={item.image01} />
                    {/* <img src={item.image02} />
                    <img src={item.image03} /> */}
                </div>
                <div className='card_header'>
                    <h4>{item.title}</h4>
                    <p>{item.categorySlug}</p>
                    <p className='price'>{item.price}</p>
                    <div className='btn'>Add to cart</div>
                </div>
                
            </div>
        </Grid>
        
    )
    return (
        <div className='home'>
            <div className='home_container'>
                <img src={panner} className="panner"></img>
            </div>
            <div className='Products'>
                <h3>Trending Now</h3>
                {listItems}
            </div>
        </div>
    )


}

export default Home
