import React from 'react';
import { useEffect, useState } from 'react';
import "./Home.scss"
import panner from '../../images/panner.png'
import productData from '../../data/Product';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import { Grid, ImageList, ImageListItem } from '@mui/material';

function srcset(image, size, rows = 1, cols = 1) {
  return {
    src: `${image}?w=${size * cols}&h=${size * rows}&fit=crop&auto=format`,
    srcSet: `${image}?w=${size * cols}&h=${
      size * rows
    }&fit=crop&auto=format&dpr=2 2x`,
  };
}

const Home = () => {
    const listItem = productData.getProducts(5)
    const Branchs = productData.getAllBranch()
    return (
        <div className='home'>
            <div className='home_container'>
                <img src={panner} className="panner"></img>
            </div>
            <h3>Trending Now</h3>
            
            <div className='Products'>
                {listItem.map((item)=> 
                    <div className='product'>
                        <div className='card' key={item.id}>
                            <div className='card_img'>
                                <img src={item.image01} className="images" />
                            </div>
                            <div className='card_header'>
                                <h4>{item.title}</h4>
                                <p>{item.categorySlug}</p>
                                <p className='price'>{item.price}</p>
                                <div className='btn'><AddShoppingCartIcon /></div>
                            </div>
                            
                        </div>
                    </div>
                )}
                {/* // {listItems} */}
            </div>

            <div className='panner'>
                <div className='information'>
                    <h1 className='infor h1'>FOREVER 21</h1>
                    <h3 className='infor h3'>Big Fashion Festival</h3>
                    <h3 className='infor h3'>70% - 80% off</h3>
                    <button className='infor'>Explore</button>
                </div>
                
            </div>


            <div className='Categories'>
                <h2>Shop by Categories</h2>
                <div>
                    <ImageList 
                    sx={{width: 1000, height: 300}}
                    variant="quilted"
                    cols={6}
                    rowHeight={125}
                    >
                        {Branchs.map((item) => (
                            <ImageListItem key={item.img} cols={item.cols || 1} rows={item.row || 1}>
                                <img {...srcset(item.img, 125, item.row, item.cols)}
                                alt = {item.title}
                                loading="lazy"/>
                            </ImageListItem>
                        ))}
                    </ImageList>
                </div>
            </div>
        </div>
    )


}

export default Home
